Box.Application.addModule('modal', function (context) {
	'use strict';
	// --------------------------------------------------------------------------
	// Private
	// --------------------------------------------------------------------------
	var $ = context.getGlobal('jQuery');
	var el = $(context.element);
	var type = el.data("modal");
	var urlModal = context.getGlobalConfig(type);
	var body = $("body");
	var iconLoaderService;
	var $modal = $('.js-ModalWindow');

    function runEvents(){
	    var $modalOverlay = $('.ModalWindow__overlay');
	    var $offsetBlock = $('.header');

    	body.on("mouseup.closeModal",function(e) {
          if ($modalOverlay.has(e.target).length === 0 && $offsetBlock.has(e.target).length === 0) {
            $modal.modalHeader('hide');
          }
        });
        body.on("click.btnCloseModal",".btnCloseModal",function(){
            $modal.modalHeader('hide');
        });
    }

    function stopEvents(){
        body.off("mouseup.closeModal");
        body.off("click.btnCloseModal");
    }

    function initModal(link){
    	var icon = $("<span data-icon='icon-close'></span>");
    	var exit = $("<div class='form__exit closeModal'></div>");
    	exit.append(icon);
    	$.getDataFromLink({
            link: link,
            callback: function(data){
            	stopEvents();
                $modal.modalHeader('init',{
                    content: data,
                    delay: 500,
                    postInit: function(el){
                    	$(".js-ModalContent > div",el).first().append(exit);
                    }
                });
            }
        });
        setTimeout(function(){
        	runEvents();
        	iconLoaderService.renderIcons();
        }, 1000);
    }

	return {
		init: function () {
			iconLoaderService = context.getService('icon-loader');
			$("body").on("openModalAuth", function(){
				var link = context.getGlobalConfig("authURL");
				initModal(link);
			});
		},
		onclick: function (event, element, elementType) {
	        if(elementType === "openModal"){
	        	initModal(urlModal);
	        }
            event.preventDefault();
		}
	}
});

